import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from '../../api/axios';
import Header from '../Common/Header';
import Footer from '../Common/Footer';
import { useAuth } from '../../contexts/AuthContext';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { v4 as uuidv4 } from 'uuid';

// 드래그&드롭용 태그 카테고리
const TAG_CATEGORIES = [
  { id: 'basic', label: '기본 정보', template: '<div class="basic-info"><h2>기본 정보</h2><p>내용을 입력하세요</p></div>' },
  { id: 'tech', label: '기술 스택', template: '<div class="tech-stack"><h2>기술 스택</h2><ul><li>기술 1</li><li>기술 2</li></ul></div>' },
  { id: 'exp', label: '수행경험', template: '<div class="experience"><h2>수행경험</h2><div class="exp-item"><h3>프로젝트명</h3><p>상세 내용</p></div></div>' },
  { id: 'career', label: '이력', template: '<div class="career"><h2>이력</h2><div class="career-item"><h3>회사명</h3><p>기간: 2020-2023</p><p>담당업무</p></div></div>' },
  { id: 'cert', label: '자격증', template: '<div class="certification"><h2>자격증</h2><ul><li>자격증 1</li><li>자격증 2</li></ul></div>' },
  { id: 'intro', label: '자기소개서', template: '<div class="introduction"><h2>자기소개서</h2><p>자기소개 내용을 입력하세요</p></div>' },
  { id: 'lang', label: '언어', template: '<div class="language"><h2>언어 능력</h2><ul><li>한국어: 모국어</li><li>영어: TOEIC 900</li></ul></div>' },
];

interface Skill { id: number; name: string; }
interface Keyword { id: number; name: string; }

const PortfolioWrite: React.FC = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [title, setTitle] = useState('');
  const [summary, setSummary] = useState('');
  const [photo, setPhoto] = useState<File | null>(null);
  const [photoUrl, setPhotoUrl] = useState<string>('');
  const [skills, setSkills] = useState<Skill[]>([]);
  const [keywords, setKeywords] = useState<Keyword[]>([]);
  const [selectedSkills, setSelectedSkills] = useState<number[]>([]);
  const [selectedKeywords, setSelectedKeywords] = useState<number[]>([]);
  const [content, setContent] = useState('');
  const [draggedTag, setDraggedTag] = useState<string | null>(null);
  const [showKeywordModal, setShowKeywordModal] = useState(false);
  const [showSkillModal, setShowSkillModal] = useState(false);
  const [tempKeyword, setTempKeyword] = useState<number[]>([]);
  const [tempSkill, setTempSkill] = useState<number[]>([]);

  useEffect(() => {
    axios.get('/skills').then(res => setSkills(res.data));
    axios.get('/keywords').then(res => setKeywords(res.data));
  }, []);

  const handlePhotoChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      setPhoto(e.target.files[0]);
      setPhotoUrl(URL.createObjectURL(e.target.files[0]));
    }
  };

  const onDragStart = (type: string) => setDraggedTag(type);

  const onDrop = (e: React.DragEvent) => {
    e.preventDefault();
    if (draggedTag) {
      const tag = TAG_CATEGORIES.find(t => t.id === draggedTag);
      if (tag) {
        const quill = (document.querySelector('.ql-editor') as HTMLElement);
        if (quill) {
          const selection = window.getSelection();
          const range = selection?.getRangeAt(0);
          if (range) {
            const fragment = document.createRange().createContextualFragment(tag.template);
            range.deleteContents();
            range.insertNode(fragment);
          } else {
            quill.innerHTML += tag.template;
          }
        }
      }
      setDraggedTag(null);
    }
  };

  const handleSave = async () => {
    const formData = new FormData();
    formData.append('title', title);
    formData.append('summary', summary);
    formData.append('content', content);
    formData.append('skills', JSON.stringify(selectedSkills));
    formData.append('keywords', JSON.stringify(selectedKeywords));
    if (photo) {
      formData.append('photo', photo);
    }

    try {
      await axios.post('/portfolios', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      alert('저장되었습니다.');
      navigate('/');
    } catch (err) {
      alert('저장 실패');
    }
  };

  return (
    <>
      <Header />
      <div style={{ display: 'flex', minHeight: '100vh', background: '#f7f8fa' }}>
        {/* 중앙: 입력 폼 */}
        <div style={{ flex: 1, padding: 40, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <div style={{ width: '100%', maxWidth: 800, background: '#fff', borderRadius: 12, boxShadow: '0 2px 8px rgba(0,0,0,0.05)', padding: 32 }}>
            <div style={{ marginBottom: 16 }}>
              <input
                type="text"
                placeholder="포트폴리오 제목"
                value={title}
                onChange={e => setTitle(e.target.value)}
                style={{ width: '100%', padding: 12, border: '1px solid #bbb', borderRadius: 8, fontSize: 18, fontWeight: 600 }}
              />
            </div>
            <div style={{ marginBottom: 16 }}>
              <textarea
                placeholder="요약"
                value={summary}
                onChange={e => setSummary(e.target.value)}
                style={{ width: '100%', padding: 12, border: '1px solid #bbb', borderRadius: 8, minHeight: 80 }}
              />
            </div>
            <div style={{ marginBottom: 16 }}>
              <input type="file" accept="image/*" onChange={handlePhotoChange} />
              {photoUrl && (
                <img
                  src={photoUrl}
                  alt="미리보기"
                  style={{ maxWidth: '100%', maxHeight: 200, marginTop: 8, borderRadius: 8 }}
                />
              )}
            </div>
            <div style={{ marginBottom: 16 }}>
              <div style={{ display: 'flex', alignItems: 'center', marginBottom: 6 }}>
                <div style={{ fontWeight: 500 }}>나의 키워드</div>
                <button
                  type="button"
                  onClick={() => setShowKeywordModal(true)}
                  style={{ marginLeft: 8, fontSize: 13, padding: '2px 12px', borderRadius: 16, border: '1px solid #bbb', background: '#f8f9fa', cursor: 'pointer' }}
                >
                  추가
                </button>
              </div>
              <div style={{ display: 'flex', flexWrap: 'wrap', gap: 8 }}>
                {keywords.filter(k => selectedKeywords.includes(k.id)).map(k => (
                  <span key={k.id} style={{ padding: '6px 14px', borderRadius: 20, border: '1px solid #ddd', background: '#007bff', color: '#fff', fontSize: 14 }}>
                    {k.name}
                  </span>
                ))}
              </div>
            </div>
            <div style={{ marginBottom: 16 }}>
              <div style={{ display: 'flex', alignItems: 'center', marginBottom: 6 }}>
                <div style={{ fontWeight: 500 }}>기술 스택</div>
                <button
                  type="button"
                  onClick={() => setShowSkillModal(true)}
                  style={{ marginLeft: 8, fontSize: 13, padding: '2px 12px', borderRadius: 16, border: '1px solid #bbb', background: '#f8f9fa', cursor: 'pointer' }}
                >
                  추가
                </button>
              </div>
              <div style={{ display: 'flex', flexWrap: 'wrap', gap: 8 }}>
                {skills.filter(s => selectedSkills.includes(s.id)).map(s => (
                  <span key={s.id} style={{ padding: '6px 14px', borderRadius: 20, border: '1px solid #ddd', background: '#007bff', color: '#fff', fontSize: 14 }}>
                    {s.name}
                  </span>
                ))}
              </div>
            </div>
            <div
              onDragOver={e => e.preventDefault()}
              onDrop={onDrop}
              style={{ marginBottom: 16 }}
            >
              <ReactQuill
                value={content}
                onChange={setContent}
                style={{ height: 400 }}
              />
            </div>
            <button
              onClick={handleSave}
              style={{ width: '100%', padding: 12, borderRadius: 8, border: 'none', background: '#007bff', color: '#fff', fontSize: 16, fontWeight: 600, cursor: 'pointer' }}
            >
              저장
            </button>
          </div>
        </div>
        {/* 우측: 태그 목록 */}
        <div style={{ width: 220, background: '#eaf3fa', borderLeft: '1px solid #e0e6ed', padding: 24 }}>
          <div style={{ fontWeight: 500, marginBottom: 16 }}>태그</div>
          <div style={{ display: 'flex', flexDirection: 'column', gap: 10 }}>
            {TAG_CATEGORIES.map(tag => (
              <div
                key={tag.id}
                draggable
                onDragStart={() => onDragStart(tag.id)}
                style={{
                  padding: '10px 0',
                  borderRadius: 8,
                  background: '#fff',
                  border: '1px solid #bcd',
                  textAlign: 'center',
                  fontWeight: 500,
                  cursor: 'grab',
                  userSelect: 'none',
                }}
              >
                {tag.label}
              </div>
            ))}
          </div>
        </div>
      </div>
      {/* 키워드 모달 */}
      {showKeywordModal && (
        <div style={{ position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', background: 'rgba(0,0,0,0.2)', zIndex: 1000, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <div style={{ background: '#fff', borderRadius: 12, padding: 32, minWidth: 320 }}>
            <div style={{ fontWeight: 600, fontSize: 18, marginBottom: 16 }}>키워드 선택</div>
            <div style={{ maxHeight: 300, overflowY: 'auto', marginBottom: 16 }}>
              {keywords.map(k => (
                <label key={k.id} style={{ display: 'block', marginBottom: 8, cursor: 'pointer' }}>
                  <input
                    type="checkbox"
                    checked={selectedKeywords.includes(k.id)}
                    onChange={() => setSelectedKeywords(prev =>
                      prev.includes(k.id) ? prev.filter(id => id !== k.id) : [...prev, k.id]
                    )}
                    style={{ marginRight: 8 }}
                  />
                  {k.name}
                </label>
              ))}
            </div>
            <div style={{ display: 'flex', justifyContent: 'flex-end', gap: 8 }}>
              <button
                onClick={() => setShowKeywordModal(false)}
                style={{ padding: '6px 18px', borderRadius: 6, border: '1px solid #bbb', background: '#f8f9fa', cursor: 'pointer' }}
              >
                취소
              </button>
              <button
                onClick={() => setShowKeywordModal(false)}
                style={{ padding: '6px 18px', borderRadius: 6, border: '1px solid #007bff', background: '#007bff', color: '#fff', cursor: 'pointer' }}
              >
                확인
              </button>
            </div>
          </div>
        </div>
      )}
      {/* 기술스택 모달 */}
      {showSkillModal && (
        <div style={{ position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', background: 'rgba(0,0,0,0.2)', zIndex: 1000, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <div style={{ background: '#fff', borderRadius: 12, padding: 32, minWidth: 320 }}>
            <div style={{ fontWeight: 600, fontSize: 18, marginBottom: 16 }}>기술 스택 선택</div>
            <div style={{ maxHeight: 300, overflowY: 'auto', marginBottom: 16 }}>
              {skills.map(s => (
                <label key={s.id} style={{ display: 'block', marginBottom: 8, cursor: 'pointer' }}>
                  <input
                    type="checkbox"
                    checked={selectedSkills.includes(s.id)}
                    onChange={() => setSelectedSkills(prev =>
                      prev.includes(s.id) ? prev.filter(id => id !== s.id) : [...prev, s.id]
                    )}
                    style={{ marginRight: 8 }}
                  />
                  {s.name}
                </label>
              ))}
            </div>
            <div style={{ display: 'flex', justifyContent: 'flex-end', gap: 8 }}>
              <button
                onClick={() => setShowSkillModal(false)}
                style={{ padding: '6px 18px', borderRadius: 6, border: '1px solid #bbb', background: '#f8f9fa', cursor: 'pointer' }}
              >
                취소
              </button>
              <button
                onClick={() => setShowSkillModal(false)}
                style={{ padding: '6px 18px', borderRadius: 6, border: '1px solid #007bff', background: '#007bff', color: '#fff', cursor: 'pointer' }}
              >
                확인
              </button>
            </div>
          </div>
        </div>
      )}
      <Footer />
    </>
  );
};

export default PortfolioWrite;
